mapper.select:
select *
from attendance


        AND curr_date = #{currDate}


        AND user_id = #{userId}

-- / 考勤状态(0正常，1迟到，2早退，3旷工，4请假，5出差） select up.id as uid, a.id as id, a.curr_date as currDate, up.name as uName, st.name as sName, a.status as status, case when a.status = 0 then '0正常' when a.status = 1 then '1迟到' when a.status = 2 then '2早退' when a.status = 3 then '3旷工' when a.status = 4 then '4请假' when a.status = 5 then '5出差' else '-1数据异常' END statusName from user_pg up left join attendance as a on up.id = a.user_id and curr_date = #{date} left join scheduling_type st on st.id = a.schedule_type_id
and up.cell_id =#{cellId}
and up.institution_id = #{institutionId}
or up.id not in (select attendance.user_id from attendance where curr_date = #{date})
order by a.id
